CREATE OR REPLACE FUNCTION ps_obter_valor_proposta_venda ( nr_seq_cliente_p bigint, ie_moeda_p text) RETURNS bigint AS $body$
DECLARE


vl_proposta_w		double precision := 0;
nr_seq_tipo_item_w		bigint;
vl_item_w			double precision;
vl_cotacao_moeda_w	cotacao_moeda.vl_cotacao%type;
qt_frequencia_w		bigint;

c01 CURSOR FOR
SELECT	i.nr_seq_tipo_item,
	coalesce(i.vl_item,0),
	coalesce(i.qt_frequencia, 1)
from	com_cliente_prop_item i,
	com_cliente_proposta p
where	i.nr_seq_proposta = p.nr_sequencia
and	p.nr_seq_cliente = nr_seq_cliente_p
and	(p.dt_liberacao IS NOT NULL AND p.dt_liberacao::text <> '')
and	coalesce(p.dt_cancelamento::text, '') = ''
and	coalesce(p.dt_vencimento,clock_timestamp()) >= clock_timestamp()
and	coalesce(i.ie_orcam_coml_wheb,'N') = 'S';


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_tipo_item_w,
			vl_item_w,
			qt_frequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			vl_proposta_w := vl_proposta_w + (vl_item_w * qt_frequencia_w);
		end;
	end loop;
	close c01;

	vl_cotacao_moeda_w 	:= obter_cotacao_moeda(ie_moeda_p);

	vl_proposta_w		:= dividir(vl_proposta_w, vl_cotacao_moeda_w);
	end;
end if;
return vl_proposta_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
