CREATE OR REPLACE FUNCTION ptu_adiciona_zero (qt_envio_p bigint, qt_casas_virgula_p bigint, qt_tamanho_campo_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Adicionar zeros antes e após o valor informado, respeitando a quantidade de casas após a vírgula.

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
- Nunca pode parar de funcionar pois é utilizada nas rotinas do PTU.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_retorno_w			varchar(255);
qt_casas_virgula_w		smallint;
qt_zero_esquerda_w		smallint;


BEGIN
if (position(',' in qt_envio_p)	> 0) then
	qt_casas_virgula_w	:= length(substr(qt_envio_p,position(',' in qt_envio_p)+1,length(qt_envio_p)));

	qt_casas_virgula_w	:= qt_casas_virgula_p - qt_casas_virgula_w;

	case	qt_casas_virgula_w
		when	0 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''));
		when	1 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'0';
		when	2 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'00';
		when	3 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'000';
		when	4 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'0000';
		else	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''));
	end case;
else
	case	qt_casas_virgula_p
		when	0 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''));
		when	1 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'0';
		when	2 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'00';
		when	3 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'000';
		when	4 	then	qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'0000';
	end case;
end if;

qt_zero_esquerda_w	:= qt_tamanho_campo_p - length(qt_retorno_w);

case	qt_zero_esquerda_w
	when	1	then	qt_retorno_w	:= '0'||qt_retorno_w;
	when	2	then	qt_retorno_w	:= '00'||qt_retorno_w;
	when	3	then	qt_retorno_w	:= '000'||qt_retorno_w;
	when	4	then	qt_retorno_w	:= '0000'||qt_retorno_w;
	when	5	then	qt_retorno_w	:= '00000'||qt_retorno_w;
	when	6	then	qt_retorno_w	:= '000000'||qt_retorno_w;
	else	qt_retorno_w	:= qt_retorno_w;
end case;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
