CREATE OR REPLACE FUNCTION ptu_converter_tipo_cliente ( ie_tipo_cliente_p text) RETURNS PTU_PEDIDO_INSISTENCIA.IE_TIPO_CLIENTE%TYPE AS $body$
BEGIN

-- Converte os dados para  somente uma letra
if (ie_tipo_cliente_p	= 'UNIMED') then
	return 'U';
elsif (ie_tipo_cliente_p	= 'PORTAL') then
