CREATE OR REPLACE FUNCTION ptu_obter_benef_a100 ( cd_unimed_p bigint, cd_carteira_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
S - Sequencia do segurado
C - Carteira inteira do segurado
N - Nome do segurado
*/
ds_retorno_w		varchar(250);
cd_unimed_w		varchar(4);
cd_carteira_usuario_w	varchar(18);
nm_beneficiario_w	varchar(255);
nr_seq_segurado_w	varchar(10);


BEGIN

if (length(cd_unimed_p) < 4) then
	select	adiciona_zeros_esquerda(cd_unimed_p,4)
	into STRICT	cd_unimed_w
	;
else
	cd_unimed_w	:= to_char(cd_unimed_p);
end if;

cd_carteira_usuario_w	:= cd_unimed_w||cd_carteira_usuario_p;

select	max(a.nm_pessoa_fisica),
	max(c.nr_sequencia)
into STRICT	nm_beneficiario_w,
	nr_seq_segurado_w
from	pls_segurado		c,
	pls_segurado_carteira	b,
	pessoa_fisica		a
where	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
and	b.nr_seq_segurado	= c.nr_sequencia
and	b.cd_usuario_plano	= cd_carteira_usuario_w;

if (ie_opcao_p	= 'S') then
	ds_retorno_w	:= nr_seq_segurado_w;
elsif (ie_opcao_p	= 'C') then
	ds_retorno_w	:= cd_carteira_usuario_w;
elsif (ie_opcao_p	= 'N') then
	ds_retorno_w	:= nm_beneficiario_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
