nr_registros_w	integer;
ds_retorno_w	varchar(4);

C01 CURSOR FOR
	SELECT	cd_doenca
	from	ptu_pedido_aut_diag_anexo
	where	nr_seq_pedido_anexo	= nr_seq_pedido_anexo_p
	order by nr_sequencia;

BEGIN
nr_registros_w	:= 1;

for r_c01_w in C01 loop
