CREATE OR REPLACE FUNCTION ptu_obter_cod_desc_inconst ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_proc_p bigint, nr_seq_mat_p bigint, ie_ordem_incons_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o código e a descrição das inconsistências do PTU

ie_opcao_p
C	= Código
D	= Descrição

ie_ordem_incons_p
Ordem da inconsistência, pois podem ser inseridas até 4.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_inconsistencia_w	ptu_inconsistencia.cd_inconsistencia%type;
ds_retorno_w		varchar(255);


BEGIN

cd_inconsistencia_w	:= ptu_obter_inconsist_trans_v50(nr_seq_guia_p,nr_seq_requisicao_p,null,'00404',ie_ordem_incons_p,nr_seq_proc_p,nr_seq_mat_p);

if (coalesce(trim(both cd_inconsistencia_w)::text, '') = '') then
	cd_inconsistencia_w	:= ptu_obter_inconsist_trans_v50(nr_seq_guia_p,nr_seq_requisicao_p,null,'00501',ie_ordem_incons_p,nr_seq_proc_p,nr_seq_mat_p);
end if;

if (cd_inconsistencia_w IS NOT NULL AND cd_inconsistencia_w::text <> '') then
	if (ie_opcao_p	= 'C') then
		ds_retorno_w	:= cd_inconsistencia_w;
	elsif (ie_opcao_p	= 'D') then
		ds_retorno_w	:= ptu_obter_desc_msg_negativa(cd_inconsistencia_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
