CREATE OR REPLACE FUNCTION ptu_obter_cod_municipio_ibge (cd_municipio_ibge_p text) RETURNS varchar AS $body$
DECLARE

cd_municipio_ibge_w  varchar(10);

BEGIN
	if (cd_municipio_ibge_p IS NOT NULL AND cd_municipio_ibge_p::text <> '') then
		select 	max(cd_municipio_ibge_ptu)
		into STRICT	cd_municipio_ibge_w
		from 	ptu_regra_municipio_ibge
		where 	cd_municipio_ibge = cd_municipio_ibge_p;
		
		if (coalesce(cd_municipio_ibge_w::text, '') = '') then
			select lpad(cd_municipio_ibge_p,6,'0') || CASE WHEN coalesce(cd_municipio_ibge_p::text, '') = '' THEN null  ELSE calcula_digito('MODULO10',lpad(cd_municipio_ibge_p,6,'0')) END
			into STRICT cd_municipio_ibge_w
			;
		end if;
	end if;
return	cd_municipio_ibge_w;

end;
$body$
LANGUAGE PLPGSQL
