CREATE OR REPLACE FUNCTION ptu_obter_dados_cobertura ( cd_tipo_cobertura_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
/*ie_opcao_p
	DS - Descrição
*/
BEGIN
if (ie_opcao_p = 'DS') then
	begin
	select	max(ds_carencia)
	into STRICT	ds_retorno_w
	from	pls_tipo_carencia
	where	cd_ptu	= cd_tipo_cobertura_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
