CREATE OR REPLACE FUNCTION ptu_obter_dados_guia_plano ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, cd_guia_p pls_guia_plano.cd_guia%type, cd_guia_referencia_p pls_guia_plano.cd_guia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000);
qt_registro_w		integer := 0;
qt_resposta_w		integer := 0;


BEGIN

if (ie_opcao_p = 'TA') then -- Tipo autorização PTU - 1 - Unimed - 2 - WSD
	if (coalesce(nr_seq_requisicao_p::text, '') = '') then
		select	count(1)
		into STRICT	qt_resposta_w
		from	ptu_resposta_autorizacao a
		where	a.nr_seq_guia	= nr_seq_guia_p;

		if (qt_resposta_w > 0) then
			select	count(1)
			into STRICT	qt_registro_w
			from	ptu_resposta_autorizacao a
			where	a.nr_seq_guia	= nr_seq_guia_p
			and	coalesce(a.ie_tipo_autorizacao,'2') <> '1';
		end if;
	else
		select	count(1)
		into STRICT	qt_resposta_w
		from	ptu_resposta_autorizacao a
		where	a.nr_seq_requisicao	= nr_seq_requisicao_p;

		if (qt_resposta_w > 0) then
			select	count(1)
			into STRICT	qt_registro_w
			from	ptu_resposta_autorizacao a
			where	a.nr_seq_requisicao	= nr_seq_requisicao_p
			and	coalesce(a.ie_tipo_autorizacao,'2') <> '1';
		end if;

	end if;

	if (qt_registro_w = 0) then
		ds_retorno_w := 1;
	else
		ds_retorno_w := 2;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
