CREATE OR REPLACE FUNCTION ptu_obter_dados_plano_benef ( nr_seq_interc_benef_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= null;
ie_abrangencia_w	ptu_intercambio_plano.ie_abrangencia%type;
nr_seq_plano_w		ptu_intercambio_benef.nr_seq_plano%type;


BEGIN

begin
select	c.nr_seq_plano,
	a.ie_abrangencia
into STRICT	nr_seq_plano_w,
	ie_abrangencia_w
from	ptu_intercambio_plano	a,
	ptu_intercambio_empresa	b,
	ptu_intercambio_benef	c
where	b.nr_sequencia	= a.nr_seq_empresa
and	b.nr_sequencia	= c.nr_seq_empresa
and	a.cd_plano_intercambio	= c.cd_plano_intercambio
and	c.nr_sequencia	= nr_seq_interc_benef_p;
exception
when others then
	ie_abrangencia_w	:= null;
end;

if (ie_opcao_p = 'A') then --AbrangÃªncia
	if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		ds_retorno_w	:= pls_obter_dados_produto(nr_seq_plano_w, 'A');
	else
		select	obter_valor_dominio(1667, CASE WHEN ie_abrangencia_w=1 THEN 'N' WHEN ie_abrangencia_w=2 THEN 'GE' WHEN ie_abrangencia_w=3 THEN 'E' WHEN ie_abrangencia_w=4 THEN 'GM' WHEN ie_abrangencia_w=5 THEN 'M'  ELSE null END  )
		into STRICT	ds_retorno_w
		;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
