CREATE OR REPLACE FUNCTION ptu_obter_dados_serv_generico ( cd_servico_p pls_serv_generico_ptu.cd_servico%type, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;


BEGIN
-- Verificar se o serviço é um código genérico
if (ie_opcao_p = 'SE') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_serv_generico_ptu
	where	cd_servico	= cd_servico_p
	and	ie_situacao	= 'A';

-- Verificar o tipo do serviço
elsif (ie_opcao_p = 'IE') then
	select	max(ie_tipo_codigo)
	into STRICT	ds_retorno_w
	from	pls_serv_generico_ptu
	where	cd_servico	= cd_servico_p
	and	ie_situacao	= 'A';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
