CREATE OR REPLACE FUNCTION ptu_obter_desc_area_atuacao ( cd_area_atuacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (cd_area_atuacao_p IS NOT NULL AND cd_area_atuacao_p::text <> '') then
	select	max(ds_area_atuacao)
	into STRICT	ds_retorno_w
	from	area_atuacao_medica
	where	cd_ptu	= cd_area_atuacao_p;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(ds_area_atuacao)
		into STRICT	ds_retorno_w
		from	area_atuacao_medica
		where	somente_numero(cd_ptu)	= cd_area_atuacao_p
		and	(cd_ptu IS NOT NULL AND cd_ptu::text <> '');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
