SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ptu_obter_desc_especialidade ( cd_especialidade_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_registro_w		bigint := 0;


BEGIN

if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	especialidade_medica
	where	cd_ptu = cd_especialidade_p;

	select	max(ds_especialidade)
	into STRICT	ds_retorno_w
	from	especialidade_medica
	where	cd_ptu = cd_especialidade_p;
end if;

/* Se houver mais de uma especialidade com mesmo código PTU, então informar aquele que esta com o IE_PTU_A500 marcado,
     senão continua buscando pelo MAX da descrição da especialidade. */
if (qt_registro_w > 1) then
	select	coalesce(max(ds_especialidade),ds_retorno_w)
	into STRICT	ds_retorno_w
	from	especialidade_medica
	where	cd_ptu		= cd_especialidade_p
	and	ie_ptu_a500	= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ptu_obter_desc_especialidade ( cd_especialidade_p text) FROM PUBLIC;

