CREATE OR REPLACE FUNCTION ptu_obter_desc_msg_negativa ( cd_inconsist_ptu_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a descrição do motivo da negativa do item
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(999);


BEGIN

begin
	select	substr(ds_inconsistencia,1,999)
	into STRICT	ds_retorno_w
	from	ptu_inconsistencia
	where	cd_inconsistencia	= cd_inconsist_ptu_p;
exception
when others then
	ds_retorno_w	:= '';
end;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
