CREATE OR REPLACE FUNCTION ptu_obter_desc_tipo_internacao (nr_seq_nota_cobranca_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

begin
	select	substr(CASE WHEN IE_TIPO_INTERNACAO=1 THEN 'Internação clínica' WHEN IE_TIPO_INTERNACAO=2 THEN 'Internação cirúgrica' WHEN IE_TIPO_INTERNACAO=3 THEN 'Internação obstrétrica' WHEN IE_TIPO_INTERNACAO=6 THEN 'Internação pediátrica' WHEN IE_TIPO_INTERNACAO=7 THEN 'Internação psiquiátrica'  ELSE '' END ,1,255)
	into STRICT	ds_retorno_w
	from	ptu_nota_hospitalar
	where	nr_seq_nota_cobr = nr_seq_nota_cobranca_p;
exception
when no_data_found then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
