CREATE OR REPLACE FUNCTION ptu_obter_desc_tipo_logradouro ( cd_ptu_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= '';
cd_tipo_logradouro_w		ptu_tipo_logradouro.cd_tipo_logradouro%type;

BEGIN

select	max(cd_tipo_logradouro)
into STRICT	cd_tipo_logradouro_w
from	ptu_tipo_logradouro
where	cd_ptu	= cd_ptu_p
and	dt_vigencia_p between coalesce(dt_inicio_vigencia,dt_vigencia_p) and coalesce(dt_fim_vigencia,dt_vigencia_p);

if (cd_tipo_logradouro_w IS NOT NULL AND cd_tipo_logradouro_w::text <> '') then
	select	max(ds_tipo_logradouro)
	into STRICT	ds_retorno_w
	from	sus_tipo_logradouro
	where	cd_tipo_logradouro = cd_tipo_logradouro_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
