CREATE OR REPLACE FUNCTION ptu_obter_ds_acomo_nota_hos (nr_seq_nota_cobranca_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

begin
	select	substr(CASE WHEN ie_tipo_acomodacao='A' THEN 'Enfermaria Intercâmbio - Plano A' WHEN ie_tipo_acomodacao='B' THEN 'Apartamento Intercâmbio - Plano B' END ,1,255)
	into STRICT	ds_retorno_w
	from	ptu_nota_hospitalar
	where	nr_seq_nota_cobr = nr_seq_nota_cobranca_p;
exception
when no_data_found then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
