CREATE OR REPLACE FUNCTION ptu_obter_dt_ultima_autor (nr_seq_guia_p pls_conta.nr_seq_guia%type) RETURNS timestamp AS $body$
DECLARE


dt_autorizacao_w	timestamp := null;

-- Obtem a data da atualização mais recente pode ocorrer em caso de internação onde podem haver guias de prorogação
BEGIN
select	coalesce(max(dt_liberacao),max(dt_autorizacao))
into STRICT	dt_autorizacao_w
from	pls_guia_plano
where	((nr_sequencia	= nr_seq_guia_p) or (nr_seq_guia_principal	= nr_seq_guia_p));

if (coalesce(dt_autorizacao_w::text, '') = '') then
	select	max(dt_liberacao)
	into STRICT	dt_autorizacao_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p;

	if (coalesce(dt_autorizacao_w::text, '') = '') then
		select	max(dt_liberacao)
		into STRICT	dt_autorizacao_w
		from	pls_guia_plano_mat
		where	nr_seq_guia	= nr_seq_guia_p;
	end if;
end if;

return	dt_autorizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
