CREATE OR REPLACE FUNCTION ptu_obter_erro_inesperado ( ds_arquivo_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w		bigint;
ds_pedido_w			varchar(255);

C01 CURSOR FOR
	SELECT	nr_seq_execucao
	from	ptu_pedido_autorizacao
	where	ds_arquivo_pedido	= ds_arquivo_p
	
union

	SELECT	nr_seq_execucao
	from	ptu_pedido_compl_aut
	where	ds_arquivo_pedido	= ds_arquivo_p
	
union

	select	nr_seq_execucao
	from	ptu_resposta_autorizacao
	where	ds_arquivo_pedido	= ds_arquivo_p
	
union

	select	nr_seq_execucao
	from	ptu_consulta_beneficiario
