	select	nr_seq_execucao
	from	ptu_resp_consulta_prest
	where	ds_arquivo_pedido	= ds_arquivo_p
	
union

	select	nr_seq_guia
	from	ptu_requisicao_ordem_serv
	where	ds_arquivo_pedido	= ds_arquivo_p
	
union

	select	nr_seq_execucao
	from	ptu_resposta_auditoria
	where	ds_arquivo_pedido	= ds_arquivo_p
	
