union

	select	nr_seq_execucao
	from	ptu_resposta_req_ord_serv
	where	ds_arquivo_pedido	= ds_arquivo_p;


BEGIN

select	substr(ds_arquivo_p,6,5)
into STRICT	ds_pedido_w
;

if (ds_pedido_w	= '00500') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_pedido_autorizacao
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w	in ('00505','00405')) then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_pedido_compl_aut
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w	= '00401') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
