	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00506') then
	select	nr_seq_guia
	into STRICT	nr_seq_retorno_w
	from	ptu_requisicao_ordem_serv
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00304') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_resposta_auditoria
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00211') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_cancelamento
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00209') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_confirmacao
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00202') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_pedido_insistencia
	where	ds_arquivo_pedido	= ds_arquivo_p;
elsif (ds_pedido_w 	= '00507') then
	select	nr_seq_execucao
	into STRICT	nr_seq_retorno_w
	from	ptu_resposta_req_ord_serv
	where	ds_arquivo_pedido	= ds_arquivo_p;
else
	open C01;
	loop
	fetch C01 into
		nr_seq_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	nr_seq_retorno_w;

end;
