CREATE OR REPLACE FUNCTION ptu_obter_estab_padrao () RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:Retornar o estabelecimento padrão definido pela Operadora
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  x] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN

--Verifica qual o estabelecimento padrão utilizado no SCS
select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	pls_end_webservice_scs
where	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '');

if (coalesce(cd_estabelecimento_w::text, '') = '') then
	select	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	pls_parametros_scs
	where	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '');

	if (coalesce(cd_estabelecimento_w::text, '') = '') then
		cd_estabelecimento_w	:= 1;
	end if;
end if;

return	cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
