CREATE OR REPLACE FUNCTION ptu_obter_modo_envio_pacote ( cd_unimed_executora_p text, cd_unimed_benef_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


--ds_retorno_w	= A - Aberto, F - Fechado.
--ie_area_atuacao_w	= 1 - Área de atuação da própria operadora, 2 - Área de atuação da operadora superior
ds_retorno_w			varchar(2);
nr_seq_regiao_w			bigint;
nr_seq_regiao_ww		bigint;
ie_area_atuacao_w		smallint;
nr_seq_congenere_w		bigint;
nr_seq_congenere_ww		bigint;


BEGIN

ie_area_atuacao_w	:= obter_valor_param_usuario(1204, 59, Obter_Perfil_Ativo, nm_usuario_p, 0);

if (ie_area_atuacao_w	= 1) then
	begin
	select	coalesce(a.nr_seq_regiao,0)
	into STRICT	nr_seq_regiao_w
	from	pls_congenere		b,
		pls_cooperativa_area	a
	where	a.nr_seq_cooperativa	= b.nr_sequencia
	and	b.cd_cooperativa	= cd_unimed_executora_p;
	exception
	when others then
		nr_seq_regiao_w	:= 0;
	end;

	begin
	select	coalesce(a.nr_seq_regiao,0)
	into STRICT	nr_seq_regiao_ww
	from	pls_congenere		b,
		pls_cooperativa_area	a
	where	a.nr_seq_cooperativa	= b.nr_sequencia
	and	b.cd_cooperativa	= cd_unimed_benef_p;
	exception
	when others then
		nr_seq_regiao_ww	:= 0;
	end;
elsif (ie_area_atuacao_w	= 2) then
	select	coalesce(nr_seq_congenere,0)
	into STRICT	nr_seq_congenere_w
	from	pls_congenere
	where	cd_cooperativa	= cd_unimed_executora_p;

	select	coalesce(nr_seq_congenere,0)
	into STRICT	nr_seq_congenere_ww
	from	pls_congenere
	where	cd_cooperativa	= cd_unimed_benef_p;

	if (nr_seq_congenere_w	<> 0) then
		begin
		select	coalesce(nr_seq_regiao,0)
		into STRICT	nr_seq_regiao_w
		from	pls_cooperativa_area
		where	nr_seq_cooperativa	= nr_seq_congenere_w;
		exception
		when others then
			nr_seq_regiao_w	:= 0;
		end;
	else
		nr_seq_regiao_w	:= 0;
	end if;

	if (nr_seq_congenere_ww	<> 0) then
		begin
		select	coalesce(nr_seq_regiao,0)
		into STRICT	nr_seq_regiao_ww
		from	pls_cooperativa_area
		where	nr_seq_cooperativa	= nr_seq_congenere_w;
		exception
		when others then
			nr_seq_regiao_ww	:= 0;
		end;
	else
		nr_seq_regiao_ww	:= 0;
	end if;
end if;

if (nr_seq_regiao_w	<> 0) and (nr_seq_regiao_ww	<> 0) and (nr_seq_regiao_w	= nr_seq_regiao_ww) then
	ds_retorno_w	:= 'F';
else
	ds_retorno_w	:= 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
