CREATE OR REPLACE FUNCTION ptu_obter_nome_anexo ( nr_seq_conta_p bigint, nr_nota_p text) RETURNS varchar AS $body$
DECLARE


dt_mes_competencia_w		varchar(6);
nr_protocolo_prestador_w	bigint;
cd_guia_w			varchar(20);
cd_cooperativa_w		varchar(4);
ds_retorno_w			varchar(42);


BEGIN
select	to_char(b.dt_mes_competencia,'MM')||to_char(b.dt_mes_competencia,'YYYY'),
	b.nr_protocolo_prestador,
	a.cd_guia,
	a.cd_cooperativa
into STRICT	dt_mes_competencia_w,
	nr_protocolo_prestador_w,
	cd_guia_w,
	cd_cooperativa_w
from	pls_conta	a,
	pls_protocolo_conta	b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia	= nr_seq_conta_p;

ds_retorno_w	:= dt_mes_competencia_w || nr_protocolo_prestador_w || cd_guia_w || cd_cooperativa_w || nr_nota_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
