CREATE OR REPLACE FUNCTION ptu_obter_nota_cobr_inco_benef ( nr_seq_nota_cobr_p ptu_nota_cobranca.nr_sequencia%type, nr_seq_serv_pre_pagto_p ptu_nota_cobranca.nr_seq_serv_pre_pagto%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10) := 'N';
qt_registros_w			bigint;


BEGIN

if (nr_seq_serv_pre_pagto_p IS NOT NULL AND nr_seq_serv_pre_pagto_p::text <> '') then
	select	count(1)
	into STRICT	qt_registros_w
	from	ptu_serv_pre_pagto_incons
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_p
	and	cd_inconsistencia	= 3  LIMIT 1;

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
