CREATE OR REPLACE FUNCTION ptu_obter_proc_mat_desc ( cd_servico_p bigint, ie_origem_proced_p bigint, ie_tipo_servico_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_servico_p	= 'P') then
	select	ds_procedimento
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento		= cd_servico_p
	and	ie_origem_proced	= ie_origem_proced_p;
elsif (ie_tipo_servico_p	= 'M') then
	select	ds_material
	into STRICT	ds_retorno_w
	from	pls_material
	where	cd_material_ops	= cd_servico_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
