CREATE OR REPLACE FUNCTION ptu_obter_qtd_env_itens_scs ( qt_envio_p bigint, qt_retorno_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_retorno_w			varchar(255);
qt_casas_virgula_w		smallint;
cd_versao_scs_w			smallint;

/* ie_opcao_p
	E - Envio
	R - Retorno
*/
BEGIN
cd_versao_scs_w	:= pls_obter_versao_scs;

if	cd_versao_scs_w = '050' then
	if (ie_opcao_p	= 'E') then
		if (position(',' in qt_envio_p)	> 0) then
			qt_casas_virgula_w	:= length(substr(qt_envio_p,position(',' in qt_envio_p)+1,length(qt_envio_p)));
			
			if	qt_casas_virgula_w = 1 then
				qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'000';
			elsif	qt_casas_virgula_w = 2 then
				qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'00';
			elsif	qt_casas_virgula_w = 3 then
				qt_retorno_w	:= to_char(replace(qt_envio_p,',',''))||'0';
			else
				qt_retorno_w	:= to_char(replace(qt_envio_p,',',''));
			end if;
		else
			qt_retorno_w	:= to_char(qt_envio_p)||'0000';
		end if;
		
	elsif (ie_opcao_p	= 'R')	then
		qt_retorno_w	:= substr(qt_retorno_p,1,4)||','||substr(qt_retorno_p,5,3);
		
		qt_casas_virgula_w	:= length(substr(qt_retorno_w,position(',' in qt_retorno_w)+1,length(qt_retorno_w)));

		if coalesce(qt_casas_virgula_w::text, '') = '' then
			qt_retorno_w 	:= qt_retorno_w || '0';
		end if;
	end if;
else
	begin
		qt_retorno_w	:= coalesce(qt_envio_p,qt_retorno_p);
	exception
	when others then
		qt_retorno_w	:= coalesce(qt_envio_p,replace(qt_retorno_p,'.',','));
	end;
end if;
	
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
