CREATE OR REPLACE FUNCTION ptu_obter_se_arq_a200_coop ( nr_seq_intercambio_p bigint, ds_arquivo_p text) RETURNS varchar AS $body$
DECLARE


cd_unimed_destino_w		varchar(10);
qt_caracter_ponto_w		bigint;
ds_tipo_arquivo_w		varchar(10);
ds_retorno_w			varchar(10);
nm_arquivo_w			varchar(255);


BEGIN

ds_retorno_w	:= 'S';

select	lpad(cd_unimed_destino,4,'0'),
	nm_arquivo
into STRICT	cd_unimed_destino_w,
	nm_arquivo_w
from	ptu_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

qt_caracter_ponto_w	:= position('.' in ds_arquivo_p);

ds_tipo_arquivo_w	:= substr(ds_arquivo_p,qt_caracter_ponto_w+1,length(ds_arquivo_p));

ds_tipo_arquivo_w	:= lpad(ds_tipo_arquivo_w,4,'0');

if (ds_tipo_arquivo_w <> cd_unimed_destino_w) then
	ds_retorno_w	:= 'N';
end if;

if (substr(nm_arquivo_w,2,length(nm_arquivo_w)) <> substr(ds_arquivo_p,2,length(ds_arquivo_p))) then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
