CREATE OR REPLACE FUNCTION ptu_obter_se_benef_existe ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
cd_pessoa_fisica_w	varchar(20);
nr_seq_segurado_w	bigint;


BEGIN
ds_retorno_w	:= 'N';

select  max(cd_pessoa_fisica),
	max(b.nr_sequencia)
into STRICT	cd_pessoa_fisica_w,
	nr_seq_segurado_w
from    pls_segurado            b,
	pls_segurado_carteira   a
where   a.nr_seq_segurado    = b.nr_sequencia
and     a.cd_estabelecimento = cd_estabelecimento_p
and     a.cd_usuario_plano   = cd_usuario_plano_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') and (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
