CREATE OR REPLACE FUNCTION ptu_obter_tec_utilizada (nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_tecnica_utilizada_w	pls_conta_proc.ie_tecnica_utilizada%type;
/*Irá retornar a tecnica utilizada no procedimento conforme o padrão PTU devido a divergência entre os padrões TISS ()1834 e PTU*/

BEGIN

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	max(ie_tecnica_utilizada)
	into STRICT	ie_tecnica_utilizada_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_conta_proc_p;

	if (ie_tecnica_utilizada_w = 'C') then
		ie_tecnica_utilizada_w	:= '1';
	elsif (ie_tecnica_utilizada_w	= 'V') then
		ie_tecnica_utilizada_w	:= '2';
	elsif (ie_tecnica_utilizada_w	= 'R') then
		ie_tecnica_utilizada_w	:= '3';
	end if;
end if;

return	ie_tecnica_utilizada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
