CREATE OR REPLACE FUNCTION ptu_obter_tipo_item ( nr_seq_pacote_reg_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            	varchar(100);
ie_tipo_item_w    		smallint;

BEGIN

select	max(ie_tipo_item)
into STRICT	ie_tipo_item_w
from	ptu_pacote_servico
where	nr_seq_pacote_reg	= nr_seq_pacote_reg_p
and 	nr_sequencia	= nr_sequencia_p;

if (ie_tipo_item_w   = 1)  then
        ds_retorno_w    := 'Taxas';
end if;

if (ie_tipo_item_w   = 2)  then
        ds_retorno_w    := 'Di√°rias';
end if;

if (ie_tipo_item_w   = 3)  then
        ds_retorno_w    := 'Gases';
end if;

if (ie_tipo_item_w   = 4)  then
        ds_retorno_w    := 'Materiais';
end if;

if (ie_tipo_item_w   = 5)  then
        ds_retorno_w    := 'Medicamentos';
end if;

if (ie_tipo_item_w   = 6)  then
        ds_retorno_w    := 'Procedimentos';
end if;


return  ds_retorno_w;

end;
