CREATE OR REPLACE FUNCTION ptu_obter_tp_ind_acidente (ie_indicacao_acidente_p pls_conta.ie_indicacao_acidente%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2);
/*Realiza um de para nas indicações de acidente para que o mesmo se encaixe nos padrões do PTU*/

BEGIN

if (ie_indicacao_acidente_p	= '0') then
	ie_retorno_w := '1';
elsif (ie_indicacao_acidente_p	= '1') then
	ie_retorno_w := '2';
elsif (ie_indicacao_acidente_p	= '2') then
	ie_retorno_w := '3';
else
	ie_retorno_w := '0';
end if;
return	ie_retorno_w;

end;
$body$
