CREATE OR REPLACE FUNCTION ptu_obter_tp_nota ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
ie_tipo_guia_w	pls_conta.ie_tipo_guia%type;
/*Realiza o de para no tipo de guia para que o mesmo se encaixe nos padr√µes do PTU*/

BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	select	ie_tipo_guia
	into STRICT	ie_tipo_guia_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_conta_p;

	if (ie_tipo_guia_w	= '3') then
		ie_retorno_w	:= '1';
	elsif (ie_tipo_guia_w = '4') then
		ie_retorno_w	:= '2';
	elsif (ie_tipo_guia_w	= '5') then
		ie_retorno_w	:= '3';
	elsif (ie_tipo_guia_w	= '6') then
		ie_retorno_w	:= '4';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
