SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ptu_obter_transacao_orig_envio ( ie_tipo_transacao_p bigint, nr_seq_transacao_p bigint) RETURNS bigint AS $body$
DECLARE

/*
ie_tipo_transacao_p
1 - Resposta de pedido de autorização ou complemento;
*/
nr_seq_origem_w	bigint;
qt_reg_aprov_w	integer;

BEGIN

if (ie_tipo_transacao_p	= 1) then
	/* Autorizado */

	select	count(1)
	into STRICT	qt_reg_aprov_w
	from	ptu_resposta_aut_servico
	where	nr_seq_pedido		= nr_seq_transacao_p
	and	ie_autorizado		= 2;

	if (qt_reg_aprov_w	= 0) then
		nr_seq_origem_w	:= 0;
	else
		select	max(nr_seq_origem)
		into STRICT	nr_seq_origem_w
		from	ptu_resposta_autorizacao
		where	nr_sequencia	= nr_seq_transacao_p;
	end if;
end if;

return	coalesce(nr_seq_origem_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ptu_obter_transacao_orig_envio ( ie_tipo_transacao_p bigint, nr_seq_transacao_p bigint) FROM PUBLIC;

