CREATE OR REPLACE FUNCTION ptu_obter_valor_env_itens_scs ( vl_item_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w			varchar(255);


BEGIN

if (vl_item_p IS NOT NULL AND vl_item_p::text <> '') then
	if (position(',' in vl_item_p)	> 0) then
		if (length(substr(vl_item_p,position(',' in vl_item_p)+1,length(vl_item_p)))	= 1) then
			vl_retorno_w	:= to_char(replace(vl_item_p,',',''))||'0';
		else
			vl_retorno_w	:= to_char(replace(vl_item_p,',',''));
		end if;
	else
		vl_retorno_w	:= to_char(vl_item_p)||'00';
	end if;

	return	vl_retorno_w;
else
	return null;
end if;
end;
$body$
LANGUAGE PLPGSQL
