CREATE OR REPLACE FUNCTION ptu_somente_caracter_permitido (ds_conteudo_p text, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retonar apenas os caracteres permitidos pelo manual PTU
-------------------------------------------------------------------------------------------------------------------

OPS - Controle de Contestacoes
Locais de chamada direta: 
[X]  Objetos do dicionario [ ]  Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao: IE_OPCAO_P

A	- Alfabetico de A a Z, maiusculas e minusculas e brancos
N	- Numerico de 0 a 9
NS	- Numerico de 0 a 9 com sinal a frente dos digitos ( - ou +) 
AN	- Alfabetico de A a Z maiusculas e minusculas e Numerico de 0 a 9 e brancos 
ANS	- Alfabetico de A a Z maiusculas e minusculas, Numerico e caracteres especiais e brancos 
ANS+	- Alfabetico de A a Z maiusculas e minusculas, Numerico, caracteres especiais, brancos, cedilha (c) e acentuacao 
ANSE	- Alfabetico de A a Z maiusculas e minusculas, Numerico e caracteres especiais e brancos ( Nao podera conter os seguintes caracteres especiais:)
TISS 	-  Alfabetico de A a Z maiusculas e minusculas, Numerico e caracteres especiais e brancos (incuindo colchetes)
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w		varchar(4000);
ds_conteudo_w		varchar(4000);


BEGIN
ds_conteudo_w := trim(both ds_conteudo_p);

if (ie_opcao_p = 'A') then
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n'),chr(198),'AE'),chr(230),'ae');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z ]', '');

elsif (ie_opcao_p = 'N') then
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'0-9]', '');

elsif (ie_opcao_p = 'NS') then
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'-+0-9]', '');

elsif (ie_opcao_p = 'AN') then
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 ]', '');

elsif (ie_opcao_p = 'ANS') then
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(replace(replace(replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n'),chr(198),'AE'),chr(230),'ae'),chr(231), 'c'), chr(199), 'C');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 '||chr(33)||chr(64)||chr(35)||chr(36)||chr(37)||chr(42)||chr(40)||chr(41)||chr(45)||chr(43)||chr(61)||chr(123)||chr(125)||chr(58)||chr(63)||chr(44)||chr(46)||chr(59)||chr(47)||chr(92)||chr(95)||''']', '');
	

elsif (ie_opcao_p = 'TISS') then

	--Original documentado pelo Giba

	/*	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
  		ds_conteudo_w := replace(replace(replace(replace(replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n'),chr(198),'AE'),chr(230),'ae'),chr(231), 'c'), chr(199), 'C');
  		ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 '||chr(33)||chr(64)||chr(35)||chr(36)||chr(37)||chr(42)||chr(40)||chr(41)||chr(45)||chr(43)||chr(61)||chr(123)||chr(125)||chr(58)||chr(63)||chr(44)||chr(46)||chr(59)||chr(47)||chr(92)||chr(95)||'\[\]'']');
	*/



	--Alterado pelo Diego
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(replace(replace(replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n'),chr(198),'AE'),chr(230),'ae'),chr(231), 'c'), chr(199), 'C');
	ds_conteudo_w := replace(replace(ds_conteudo_w, chr(91), 'Philipsabrecolchete'), chr(93), 'Philipsfechacolchete');
	ds_conteudo_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 '||chr(33)||chr(64)||chr(35)||chr(36)||chr(37)||chr(42)||chr(40)||chr(41)||chr(45)||chr(43)||chr(61)||chr(123)||chr(125)||chr(58)||chr(63)||chr(44)||chr(46)||chr(59)||chr(47)||chr(92)||chr(95)||''']', '');
	ds_conteudo_w := replace(ds_conteudo_w, 'Philipsabrecolchete', chr(91));
	ds_conteudo_w := replace(ds_conteudo_w, 'Philipsfechacolchete', chr(93));
	ds_retorno_w := ds_conteudo_w;

elsif (ie_opcao_p = 'ANS+') then
	ds_conteudo_w := replace(replace(ds_conteudo_w,chr(198),'AE'),chr(230),'ae');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 '||chr(33)||chr(64)||chr(35)||chr(36)||chr(37)||chr(42)||chr(40)||chr(41)||chr(45)||chr(43)||chr(61)||chr(123)||chr(125)||chr(58)||chr(63)||chr(44)||chr(46)||chr(59)||chr(47)||chr(92)||chr(95)||''']', '');
	
elsif (ie_opcao_p = 'ANSE') then
	ds_conteudo_w	:= elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w	:= replace(replace(replace(replace(replace(replace(ds_conteudo_w,chr(209),'N'),chr(241),'n'),chr(198),'AE'),chr(230),'ae'),chr(231), 'c'), chr(199), 'C');
	ds_conteudo_w	:= replace(replace(replace(replace(replace(replace(replace(ds_conteudo_w,chr(42),''),chr(60),''),chr(62),''),chr(58),''),chr(63),''),'/',''),'\','');		--'
	ds_retorno_w	:= regexp_replace(ds_conteudo_w, '['||chr(94)||'a-zA-Z0-9 '||chr(33)||chr(64)||chr(35)||chr(36)||chr(37)||chr(38)||chr(42)||chr(40)||chr(41)||chr(45)||chr(43)||chr(61)||chr(123)||chr(125)||chr(60)||chr(62)||chr(58)||chr(63)||chr(44)||chr(46)||chr(59)||chr(47)||chr(92)||chr(95)||''']', '');

end if;

return	ds_retorno_w;

end;
$body$
