CREATE OR REPLACE FUNCTION qr_checklist_responsavel (nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


cd_funcao_w					qr_objeto_schematic.cd_funcao%type;
nr_seq_checklist_item_w		qr_checklist_funcao.nr_seq_checklist_item%type;
ds_retorno_w				varchar(255);


BEGIN
select	max(cd_funcao),
		max(nr_seq_checklist_item)
into STRICT	cd_funcao_w,
		nr_seq_checklist_item_w
from	qr_checklist_funcao a,
		qr_objeto_schematic b
where	a.nr_sequencia = nr_sequencia_p
and		a.nr_seq_objeto = b.nr_sequencia;

select	max(nm_usuario_regra)
into STRICT	ds_retorno_w
from	qr_checklist_usuario a,
		qr_checklist_item b,
		qr_checklist_funcao_usu c
where	b.nr_sequencia = nr_seq_checklist_item_w
and		c.cd_funcao = cd_funcao_w
and		a.nr_seq_checklist_item = b.nr_sequencia
and		c.nr_seq_usu_checklist = a.nr_sequencia;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
