CREATE OR REPLACE FUNCTION qr_obter_etapa_sup (ie_opcao_p text, nr_seq_result_p bigint) RETURNS bigint AS $body$
DECLARE


cd_funcao_w	integer;
nr_seq_etapa_w	bigint;


BEGIN
if (ie_opcao_p = 'C') then
	select	max(c.cd_funcao)
	into STRICT	cd_funcao_w
	from	qr_checklist_result a,
			qr_checklist_funcao b,
			qr_objeto_schematic c
	where	a.nr_sequencia = nr_seq_result_p
	and		a.nr_seq_checklist_funcao = b.nr_sequencia
	and		b.nr_seq_objeto = c.nr_sequencia;


	select	max(nr_seq_etapa_sup)
	into STRICT	nr_seq_etapa_w
	from	qr_regra_ordem_servico
	where	cd_funcao = cd_funcao_w;

elsif (ie_opcao_p = 'P') then
	select	max(cd_funcao)
	into STRICT	cd_funcao_w
	from	qr_parametro_result
	where	nr_sequencia = nr_seq_result_p;

	select	max(nr_seq_etapa_sup)
	into STRICT	nr_seq_etapa_w
	from	qr_regra_ordem_servico
	where	cd_funcao = cd_funcao_w;

end if;
return	nr_seq_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
