CREATE OR REPLACE FUNCTION qr_obter_info_check_item (nr_seq_item_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000) := '';
ds_checklist_w	varchar(255);


BEGIN
if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	max(ds_checklist)
	into STRICT	ds_checklist_w
	from	qr_checklist a
	where	nr_sequencia = (SELECT	max(nr_seq_checklist)
							from	qr_checklist_item b
							where	b.nr_sequencia = nr_seq_item_p);

	select	CASE WHEN ie_tipo_p='DC' THEN ds_checklist_item WHEN ie_tipo_p='DD' THEN ds_detalhamento WHEN ie_tipo_p='DO' THEN ds_observacao WHEN ie_tipo_p='DK' THEN ds_checklist_w END
	into STRICT	ds_retorno_w
	from	qr_checklist_item
	where	nr_sequencia = nr_seq_item_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
