SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qr_obter_se_status_ok (nr_seq_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_reg_w		bigint;
ie_retorno_w	varchar(255);

/*
P = Pendente
E = Erro
S = OK
*/
BEGIN

ds_retorno_w	:= obter_desc_expressao(295406);
ie_retorno_w	:= 'P';


select	count(*)
into STRICT	qt_reg_w
from	qr_checklist_result
where	ie_finalizado = 'N'
and		nr_seq_checklist_funcao = nr_seq_item_p;

if (qt_reg_w > 0) then
	ds_retorno_w := obter_desc_expressao(289370);
	ie_retorno_w := 'E';
else
	select	count(*)
	into STRICT	qt_reg_w
	from	qr_checklist_result
	where	ie_finalizado = 'S'
	and		ie_tipo_resultado = 'S'
	and		nr_seq_checklist_funcao = nr_seq_item_p;

	if (qt_reg_w > 0) then
		ds_retorno_w	:= obter_desc_expressao(311702);
		ie_retorno_w	:= 'S';
	end if;
end if;

if (ie_opcao_p <> 'D') then
	ds_retorno_w := ie_retorno_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION qr_obter_se_status_ok (nr_seq_item_p bigint, ie_opcao_p text) FROM PUBLIC;

