SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qsm_alerta_alta_medica (cd_estabelecimento_p bigint, nr_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


quebra_w				varchar(10)	:= chr(13)||chr(10);
ds_erro_w				varchar(2000);
ie_tipo_convenio_w		bigint;
cd_convenio_w			integer;
ie_tipo_atendimento_w	atendimento_paciente.ie_tipo_atendimento%type;
cd_setor_atendimento_w	integer;
ie_lista_tarefas_w		consistencia_alta_medica.ie_lista_tarefas%type;
qt_lista_inserida_w		bigint := 0;

C01 CURSOR FOR
	SELECT	distinct obter_valor_dominio(8672, c.cd_categoria) ds_categoria_p
	from	wl_worklist a,
			wl_regra_item b,
			wl_item c
	where	a.nr_seq_regra = b.nr_sequencia
	and     a.NR_SEQ_ITEM = c.nr_sequencia
	and		coalesce(a.dt_final_real::text, '') = ''
	and		a.nr_atendimento = nr_atendimento_p
	and		coalesce(c.ie_situacao,'A') = 'A'
	and		coalesce(b.ie_situacao, 'A') = 'A'
	and		b.ie_considerar_alta = 'A'
	and		c.cd_categoria <> 'MT';
	

C02 CURSOR FOR
	SELECT	distinct obter_valor_dominio(8672, c.cd_categoria) ds_categoria_p
	from	wl_worklist a,
			wl_regra_item b,
			wl_item c
	where	a.nr_seq_regra = b.nr_sequencia
	and     a.NR_SEQ_ITEM = c.nr_sequencia
	and		coalesce(a.dt_final_real::text, '') = ''
	and		((a.cd_pessoa_fisica = obter_pessoa_atendimento(nr_atendimento_p,'C')) or (a.nr_atendimento = nr_atendimento_p))
	and		coalesce(c.ie_situacao,'A') = 'A'
	and		coalesce(b.ie_situacao, 'A') = 'A'
	and		b.ie_considerar_alta = 'A'
	and		c.cd_categoria = 'MT';

BEGIN

	select	max(ie_tipo_atendimento),
			max(obter_setor_atendimento(nr_atendimento)),
			max(Obter_Tipo_Convenio(Obter_Convenio_Atendimento(nr_atendimento))),
			max(Obter_Convenio_Atendimento(nr_atendimento))
	into STRICT	ie_tipo_atendimento_w,
			cd_setor_atendimento_w,
			ie_tipo_convenio_w,
			cd_convenio_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_p;

	select	coalesce(max(ie_lista_tarefas),'N')
	into STRICT	ie_lista_tarefas_w
	from	consistencia_alta_medica
	where	cd_estabelecimento	= cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w
	and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
	and		coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0)) = coalesce(ie_tipo_convenio_w,0)
	and		coalesce(cd_convenio, coalesce(cd_convenio_w,0)) = coalesce(cd_convenio_w,0)
	and		coalesce(nr_seq_teste_vacina,0) = 0
	order by coalesce(ie_tipo_atendimento,0), coalesce(cd_setor_atendimento,0);
	

	if (ie_lista_tarefas_w = 'S') then
		for c01_w in C01 loop
				begin
					if (qt_lista_inserida_w = 0) then
						ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(1077019) || quebra_w;
						qt_lista_inserida_w := qt_lista_inserida_w + 1;
					end if;
					

					ds_erro_w := ds_erro_w || ' - ' || c01_w.DS_CATEGORIA_P || quebra_w;
				end;
			end loop;
			for c02_w in C02 loop
				begin
					if (qt_lista_inserida_w = 0) then
						ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(1036223) || quebra_w;
						qt_lista_inserida_w := qt_lista_inserida_w + 1;
					end if;
					

					ds_erro_w := ds_erro_w || ' - ' || c02_w.DS_CATEGORIA_P || quebra_w;
				end;
			end loop;
	end if;
	

	return ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION qsm_alerta_alta_medica (cd_estabelecimento_p bigint, nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

