SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qsm_get_status_task_doc (nr_document_p bigint) RETURNS varchar AS $body$
DECLARE


/*
OK -> All documents are OK
WN -> Any document with warning
TD -> Any document with things to do
CC -> Any cancelled document 
*/
const_documents_ok 			varchar(2) := 'OK';
const_documents_error 		varchar(2) := 'TD';
const_documents_na			varchar(2) := 'NA';
const_documents_warning     varchar(2) := 'WN';
const_documents_cancelled   varchar(2) := 'CC';
ds_retorno_w				varchar(2);
nr_seq_document_w			episodio_pac_document.nr_sequencia%type;
ie_status_w					episodio_pac_doc_status.ie_status%type;


BEGIN
		select	coalesce(max('S'),'N')
			into STRICT	ie_status_w
			from	episodio_pac_doc_status
			where	nr_seq_document = nr_document_p
			and		ie_status = 'CANCELLED';
		if (ie_status_w = 'S')then
			return const_documents_cancelled;
		end if;
		
		select	coalesce(max('S'),'N')
			into STRICT	ie_status_w
			from	episodio_pac_doc_status
			where	nr_seq_document = nr_document_p
			and		ie_status = 'WARNING'
			and		cd_perfil = obter_perfil_ativo;
		if (ie_status_w = 'S')then
			return const_documents_warning;
		end if;
		
		select	coalesce(max('S'),'N')
			into STRICT	ie_status_w
			from	episodio_pac_doc_status
			where	nr_seq_document = nr_document_p
			and		ie_status = 'ERROR'
			and		cd_perfil = obter_perfil_ativo;
		if (ie_status_w = 'S')then
			return const_documents_error;
		end if;
		
		select	coalesce(max('S'),'N')
			into STRICT	ie_status_w
			from	episodio_pac_doc_status
			where	nr_seq_document = nr_document_p
			and		ie_status = 'OK'
			and		cd_perfil = obter_perfil_ativo;
		if (ie_status_w = 'S')then
			return const_documents_ok;
		end if;
		
	return const_documents_na;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
