CREATE OR REPLACE FUNCTION qtd_falta_lista_esp (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


	qtd_itens_w bigint := 0;


BEGIN

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		select count(1)
		into STRICT qtd_itens_w
		from ag_lista_espera_contato b
		where b.nr_seq_lista_espera = nr_sequencia_p
		and b.nr_seq_situacao_atual in (
		  SELECT c.nr_sequencia
		  from lista_espera_situacao_cont c
		  where c.ie_tipo_contato = 'PF'
		);
	end if;

	return qtd_itens_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
