CREATE OR REPLACE FUNCTION qt_consiste_hora_disp ( dt_agenda_p timestamp, nr_duracao_p bigint, cd_Estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_local_w 	qt_local.nr_sequencia%type;
qt_agenda_marcacao_w		bigint;
qt_agendamento_quimio_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_Sequencia
	from	qt_local a
	where	a.ie_situacao = 'A'
	and		a.cd_estabelecimento = cd_Estabelecimento_p
	and		qt_verifica_turno(a.nr_Sequencia, dt_agenda_p, nr_duracao_p) = 'S'
	order by a.NR_SEQ_APRES;


BEGIN

open C01;
loop
fetch C01 into
	nr_Seq_local_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	count(*)
	into STRICT	qt_agenda_marcacao_w
	from	agenda_quimio_marcacao
	where	nr_seq_local	= nr_Seq_local_w
	and		( ( dt_agenda between dt_Agenda_p and dt_Agenda_p + ( nr_duracao_p -1) /1440)
	or		( ( dt_agenda + ( nr_duracao -1)/1440) between dt_Agenda_p and dt_Agenda_p + nr_duracao_p  /1440)
	or		( dt_Agenda_p between dt_agenda and dt_agenda + ( nr_duracao -1) /1440)
	or		( dt_Agenda_p + ( nr_duracao_p -1)/1440) between dt_agenda and dt_agenda + nr_duracao /1400 );

	if (qt_agenda_marcacao_w = 0) then
		select 	count(*)
		into STRICT	qt_agendamento_quimio_w
		from	agenda_quimio
		where	nr_seq_local	= nr_Seq_local_w
		and		( ( dt_agenda between dt_Agenda_p and dt_Agenda_p + ( nr_duracao_p -1) /1440)
		or		( ( dt_agenda + ( nr_minuto_duracao -1)/1440) between dt_Agenda_p and dt_Agenda_p + nr_duracao_p  /1440)
		or		( dt_Agenda_p between dt_agenda and dt_agenda + ( nr_minuto_duracao -1) /1440)
		or		( dt_Agenda_p + ( nr_duracao_p -1)/1440) between dt_agenda and dt_agenda + nr_minuto_duracao /1400 );
	end if;

	if (qt_agenda_marcacao_w = 0 and qt_agendamento_quimio_w = 0) then
		return nr_seq_local_w;
	end if;
	end;
end loop;
close C01;
nr_seq_local_w := null;

return	nr_seq_local_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
