CREATE OR REPLACE FUNCTION qt_dados_age_cons ( nr_seq_atendimento_p bigint, dt_agendamento_p timestamp, ie_retorno_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);

BEGIN

if (coalesce(nr_seq_atendimento_p,0) > 0) and (coalesce(ie_retorno_p,0) > 0) and (dt_agendamento_p IS NOT NULL AND dt_agendamento_p::text <> '') then

	if (ie_retorno_p = 1) then

		select	substr(max(obter_nome_usuario(nm_usuario_origem)),1,255)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and	dt_agenda between trunc(dt_agendamento_p) and trunc(dt_agendamento_p) + 86399/86400;

	elsif (ie_retorno_p = 2) then

		select	substr(max(nm_usuario_origem),1,255)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and	dt_agenda between trunc(dt_agendamento_p) and trunc(dt_agendamento_p) + 86399/86400;

	elsif (ie_retorno_p = 3) then

		select	substr(max(obter_nome_usuario(nm_usuario)),1,255)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and	dt_agenda between trunc(dt_agendamento_p) and trunc(dt_agendamento_p) + 86399/86400;

	elsif (ie_retorno_p = 4) then

		select	substr(max(nm_usuario),1,255)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and	dt_agenda between trunc(dt_agendamento_p) and trunc(dt_agendamento_p) + 86399/86400;

	elsif (ie_retorno_p = 5) then

		select	substr(max(nm_usuario_confirm),1,255)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and	dt_agenda between trunc(dt_agendamento_p) and trunc(dt_agendamento_p) + 86399/86400;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
