CREATE OR REPLACE FUNCTION qt_item_ordem_cancelados (nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


	ds_retorno_w	bigint;


BEGIN

if	(nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '' AND nr_item_oci_p IS NOT NULL AND nr_item_oci_p::text <> '') then
	begin

	select 	count(*) qt_reg
	into STRICT	ds_retorno_w
	from 	ordem_compra_item_entrega
	where 	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '')
	and   	nr_ordem_compra = nr_ordem_compra_p
	and   	nr_item_oci = nr_item_oci_p;

	end;
end if;


return	ds_retorno_w;

end;
