CREATE OR REPLACE FUNCTION qt_itens_solic_em_cotacao ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;			
			

BEGIN			

select	sum(qt_material)
into STRICT	qt_material_w
from (
	SELECT	sum(b.qt_material) qt_material
	from	cot_compra a,
		cot_compra_item b
	where	a.nr_cot_compra = b.nr_cot_compra
	and	nr_solic_compra = nr_solic_compra_p
	and	nr_item_solic_compra = nr_item_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
	
union all

	SELECT	sum(b.qt_material) qt_material
	from	cot_compra a,
		cot_compra_item b,
		cot_compra_solic_agrup c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	b.nr_cot_compra = c.nr_cot_compra
	and	b.nr_item_cot_compra = c.nr_item_cot_compra
	and	c.nr_solic_compra = nr_solic_compra_p
	and	c.nr_item_solic_compra = nr_item_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '') alias5;
	
return	coalesce(qt_material_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
