CREATE OR REPLACE FUNCTION qt_obser_se_ciclo_inativo (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_inativo_w	varchar(1);
ie_status_w	varchar(1);

BEGIN

select	max(ie_status)
into STRICT	ie_status_w
from	paciente_setor
where	nr_seq_paciente = nr_seq_paciente_p;

if (ie_status_w = 'I') then
