CREATE OR REPLACE FUNCTION qt_obter_classif_quimio (ds_dia_ciclo_p text, nr_seq_prot_medic_p bigint, cd_protocolo_p bigint, nr_seq_atendimento_p bigint, dt_agenda_p timestamp, nr_seq_pendencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w			bigint;
ds_classif_w			varchar(255);
qt_retorno_w			bigint;


BEGIN

select	count(*)
into STRICT 	qt_regra_w
from	qt_classif_duracao;

if (qt_regra_w > 0) then

	qt_retorno_w	:= qt_obter_dur_aplicacao(ds_dia_ciclo_p,
						nr_seq_prot_medic_p,
						cd_protocolo_p,
						nr_seq_atendimento_p,
						dt_agenda_p,
						nr_seq_pendencia_p,
						nm_usuario_p,
						cd_estabelecimento_p);

	select	max(ds_classificacao)
	into STRICT	ds_classif_w
	from	qt_classif_duracao
	where	nr_minuto_duracao = qt_retorno_w;

end if;

return	ds_classif_w;

end;
$body$
